ccess_token";		$access_token = get_option( $access_token_option );	$api_key_option = "constant_contact_api_key";		$api_key = get_option( $api_key_option );			$url="https://api.constantcontact.com/v2/eventspot/events?limit=100&api_key=$api_key&access_token=$access_token";		$events_list = cies_getEvents($url);					$fetch = $events_list['results'];	for($i=0;$i<count($fetch);$i++)		{	?>        <tr id="event_id_<?php echo $fetch[$i]['id'];?>">            <td width="300px">				<?php echo $fetch[$i]['title'];?>			</td>			<td width="75"><?php echo date('m-d-Y',strtotime($fetch[$i]['start_date']));?></td>			<td width="100">				<?php 				if($fetch[$i]['status']=='ACTIVE')				{					echo "<span id='active_status_".$fetch[$i]['id']."' style='color:green;'>".$fetch[$i]['status']."</span>";				}				if($fetch[$i]['status']=='CANCELLED')				{					echo "<span id='cancel_status_".$fetch[$i]['id']."' style='color:red;'>".$fetch[$i]['status']."</span>";				}				if($fetch[$i]['status']=='DRAFT')				{					echo "<span id='draft_status_".$fetch[$i]['id']."' style='color:#000;'>".$fetch[$i]['status']."</span>";				}				?>			</td>			<td width="100px">				<?php				$url = "https://events.r20.constantcontact.com/register/eventReg?oeidk".$fetch[$i]['event_detail_url'] ."&oseq=&c=&ch=";									$event_link = str_replace("/v2/eventspot/events/","=",$url);				?>				<ul>					<li>					<a title="share on facebook" target="_blank" href="http://www.facebook.com/sharer/sharer.php?u=<?=$event_link;?>">					<img title="share on facebook" alt="share on facebook" src="<?php echo plugins_url( 'images/fb_share.png' , dirname(__FILE__ )); ?>">					</a>					</li>					<li>						<a title="share on google plus" target="_blank" href="http://plus.google.com/share?url=<?=$event_link;?>">					<img title="share on google plus" alt="share on google plus" src="<?php echo plugins_url( 'images/google-share.png' , dirname(__FILE__ )); ?>">					</a>					</li>					<li>					<a title="share on Twitter" target="_blank" href="http://twitter.com/share?url=<?=$event_link;?>"><img title="share on facebook" alt="share on facebook" src="<?php echo plugins_url( 'images/twitter_share.png' , dirname(__FILE__ )); ?>"></a>					</li>				</ul>			</td>            <td>            <a href="?page=es_update_event&event_id=<?php echo $fetch[$i]['id'];?>" class="es-edit-btn">Edit</a>			<?php			if($fetch[$i]['status']=='ACTIVE')			{			?>	            <a title="click to cancel this event" id="publish_btn_<?php echo $fetch[$i]['id'];?>" onclick="es_delete_event('<?php echo $fetch[$i]['id'];?>')"  class="es-green-btn">Published</a>			<?php } ?>			<?php			if($fetch[$i]['status']=='CANCELLED')			{			?>	            			<a title="Event is canceled" id="cancel_btn_<?php echo $fetch[$i]['id'];?>"  class="es-del-btn">Canceled</a>			<?php } ?>			<?php			if($fetch[$i]['status']=='DRAFT')			{			?>	            			<a title="click to publish this event" id="draft_btn_<?php echo $fetch[$i]['id'];?>"  onclick="es_publish_event('<?php echo $fetch[$i]['id'];?>')"  class="es-draft-btn">DRAFT</a>			<?php } ?>			            </td>        </tr> 	<?php		}	?>		<?php	if($api_key=='' && $access_token=='')		{	?>	<tr>		<td>&nbsp;</td>		<td colspan="2">		<?php		if($api_key=='' && $access_token=='')		{			echo "<h3 style='text-align:center;'>please save API settings <a href='?page=es_settings_page'> here</a></h3>";					}		else		{			if(count($fetch)==0)			{				echo "<h3 style='text-align:center;'>There are no events Added yet please add by clicking Create Event link above ^</h3>";			}		}				?>		</td>		<td>&nbsp;</td>		<td>&nbsp;</td>	</tr>	<?php } ?>    </tbody>	</table>	<div id="publish_msg" class="isa_error">			 	</div>	<input type="hidden" name="plugin_url"  id="plugin_url" value="<?php echo plugins_url( 'images' , dirname(__FILE__ )); ?>" /></div></div>